def calculate_frequencies(file_contents):
    # Here is a list of punctuations and uninteresting words you can use to process your text
    punctuations = '''!()-[]{};:'"\,<>./?@#$%^&*_~'''
    uninteresting_words = ["the", "a", "to", "if", "is", "it", "of", "and", "or", "an", "as", "i", "me", "my", \
    "we", "our", "ours", "you", "your", "yours", "he", "she", "him", "his", "her", "hers", "its", "they", "them", \
    "their", "what", "which", "who", "whom", "this", "that", "am", "are", "was", "were", "be", "been", "being", \
    "have", "has", "had", "do", "does", "did", "but", "at", "by", "with", "from", "here", "when", "where", "how", \
    "all", "any", "both", "each", "few", "more", "some", "such", "no", "nor", "too", "very", "can", "will", "just"]

    # LEARNER CODE START HERE
    def word_sanitize(word):
        new_word = ""
        for letter in word:
            if letter not in punctuations:
                new_word = new_word + letter.lower()
        return new_word

    myWords = {}

    wordList = file_contents.split()

    for word in wordList:
        san_word = word_sanitize(word)
        if san_word not in uninteresting_words:
            if san_word not in myWords:
                myWords[san_word] = 0
            myWords[san_word] += 1




    #wordcloud
    cloud = wordcloud.WordCloud()
    cloud.generate_from_frequencies(myWords)
    return cloud.to_array()
